
public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		switch ( a )
		
		{
		case 10 :
			System.out.println("10이다");
			break;
			
		case 20:
			System.out.println("20이다");
			break;
			
		case 30:
			System.out.println("30이다");
			break;
			
		default:
			System.out.println("그 이외의 값");
			break;
			
		}	
			
		int b = 10;
		if (b == 10)
		{
			System.out.println("10이다");
		
		}
		
		else if ( b == 20) {
			System.out.println("20이다");
		}
		
		else if ( b == 30) 
		{
			System.out.println("30이다");
			
		}
		
		
		
		else {
			
			System.out.println("그 이외의 값");
		
		
		}
		
		
		
	}
	
	
}
